var books = document.getElementsByClassName("booklist-container");
var bookBtnCart = document.getElementsByClassName("addtocart-button");

for(var i = 0; i < books.length; i++ ) {
    books[i].addEventListener("click", e => onBookClick(e));
    bookBtnCart[i].addEventListener("click", e => addBookToCart(e));
}

function onBookClick(e) {
    var id = e.currentTarget.getAttribute("value");
    window.location.href = "/book/details/" + id;
}

document.addEventListener("DOMContentLoaded", function(event) { 
    getCookie();
  });